sp_angular_provider.script:
function ipsHttpServicice($rootScope) {
    this.update = async function (params) {
        var res = await $rootScope.server.get(params);
        return res.data;
    }

    this.createObjetive = async function (data) {
        var res = await $rootScope.server.get({
            action: 'create_objective',
            data: data
        });

        console.log(res.data.response);
        return res.data.response;
    }


    this.createEvaluation = async function (data) {
        var res = await $rootScope.server.get({
            action: 'create_evaluation',
            data: data
        });
        return res;
    }

    this.createOrUpdateInitiative = async function (data) {


        var res = await $rootScope.server.get({
            action: 'create_or_update_initiative',
            data: data
        });

        return res.data.response;
    }

    this.closeEvaluation = function () {
        delete $rootScope.data.evaluation;
        $rootScope.data = $rootScope.data

    }

    this.sendEvaluation = async function () {

        var res = await $rootScope.server.get({
            action: 'sendEvaluation',
            data: $rootScope.data.evaluation
        });

        return res.data.response;

    }

}
