sp_angular_provider.script:
function ipsValidator($rootScope, spUtil, $http) {


    /**
    *
    */
    this.validate = async function (value, field, validations) {
        var validation = await this.validateField(
            field,
            value,
            validations
        );

        if (validation.message) {
            // _.debounce(function () {

            // 	spUtil.addErrorMessage(validation.message);
            // }, 500);
            return validation.errors[field][0];
        }
        return '';
    }



    this.validateField = async function (field, value, validations) {
        try {
            var res = await $http({
                url: '/api/x_ipsu2_ips_perfor/v1/ips_assessments_evaluation/validate-fields',
                method: 'POST',
                data: {
                    field: field,
                    value: value,
                    validations: validations
                },
                headers: {
                    "Content-Type": "application/json"
                }
            });
            return res.data.result;
        } catch (e) {
            spUtil.addErrorMessage(e);
        }
    }
}
