sp_angular_provider.script:
function IPSStepper($http) {

    return {
        restrict: 'E',
        scope: {
            assessment: '<',
            state: '<'
        },
        link: async function (scope) {


            scope.$watch('state', async function (newValue) {
                init();
            })
            init();
            async function init(params) {
                const stepper = await $http({
                    url: '/api/x_ipsu2_ips_perfor/ips_assessments_evaluation/ips-get-stepper/' + scope.assessment,
                    method: 'get',
                    headers: {
                        "Content-Type": "application/json"
                    }
                });
                scope.stepper = stepper.data.result;

                scope.$apply();
            }
        },
        templateUrl: 'IPSStepper'
    };
}
