sp_angular_provider.script:
function ipsCreateObejetive($uibModal, $rootScope, ipsValidator) {
    return {
        restrict: 'E',
        scope: {

        },

        link: function (scope) {
            scope.data = $rootScope.i_data.objetiveForm;
            scope.validations = $rootScope.i_data.errors;

            scope.$watch('data.objetivo', async function (newValue) {
                await ipsValidator.validate(newValue, 'objetivo', [
                    'required',
                    'required'
                ]);

            });

            scope.newObjective = function () {
                scope.createObjetive();
                scope.createOrEditObejetive = "${Crear nuevo objetivo}"
            }

            scope.createObjetive = function () {

                scope.intrucciones = $uibModal.open({
                    templateUrl: 'modalCreateObjetive',
                    scope: scope
                });
            };

            scope.closecreateObjetive = function () {
                scope.intrucciones.dismiss();
                scope.intrucciones = null;
            };

        },

        templateUrl: 'IpsCreateObjetive'
    };
}
