sp_angular_provider.script:
function ipsRating() {

    return {
        restrict: 'E',
        scope: {
            averangeEvaluation: '<',
            type: '<',
            averangeColaborador: '<',
        },
        link: function (scope) {
            scope.averange = 0;
            if (scope.type == 1) {

                scope.averangeEvaluationInteger = 0;
                scope.$watch('averangeColaborador', function (newValue) {
                    scope.averange = newValue || 0;

                    var porcentaje = parseInt(newValue) || 0;
                    if (porcentaje < 0) {

                        scope.averangeEvaluationInteger = 0
                    } else if (porcentaje > 100) {
                        scope.averangeEvaluationInteger = 100;
                    }
                    else {
                        scope.averangeEvaluationInteger = porcentaje;
                    }
                });
            }

            if (scope.type == 2) {

                scope.averangeEvaluationInteger = 0;
                scope.$watch('averangeEvaluation', function (newValue) {
                    scope.averange = newValue || 0;
                    var porcentaje = parseInt(newValue) || 0;
                    if (porcentaje < 0) {

                        scope.averangeEvaluationInteger = 0
                    } else if (porcentaje > 100) {
                        scope.averangeEvaluationInteger = 100;
                    }
                    else {
                        scope.averangeEvaluationInteger = porcentaje;
                    }
                });
            }




        },



        templateUrl: 'IPSRating'
    };
}
