sys_script_include.script:
var IPSPerformanceEvaluation = Class.create();
IPSPerformanceEvaluation.prototype = {

    evaluations: [],


    initialize: function (settings) {
        this.settings = settings;
    },


    getEvaluations: function (paginator) {
        var p = this.getPaginator(paginator)
        var records = [];
        var evaluations = new GlideRecord('x_ipsu2_ips_perfor_performance_evaluation');
        evaluations.addQuery('assigned_to', gs.getUserID());
        evaluations.orderByDesc('opened_at');

        evaluations.query();
        evaluations.setLocation(p.setLocation);
        var count = 0;


        while (evaluations.next() && count < p.last_record) {
            records.push(evaluation);
            count++;
        }

        return records;
    },

    getPaginator: function (paginator) {
        var p = {};

        if (paginator.current_page <= 0) {
            p.setLocation = -1;
            p.last_record = paginator.records_per_page

            return p;
        }

        p.setLocation = current_page * paginator.records_per_page;

        p.last_record = ((current_page * paginator.records_per_page) + 1) + paginator.records_per_page;

        return p;
    },


    type: 'IPSPerformanceEvaluation'
};
