sys_script_include.script:
var IPSModelFactory = Class.create();
IPSModelFactory.prototype = {

    find: function (id) {
        var initiative = new GlideRecord(this.table);
        initiative.get(id);
        return initiative;
    },

    create: function (data) {
        var iniciative = new GlideRecord(this.table);
        iniciative.initialize();
        for (var id in data) {
            iniciative[id] = data[id];
        }
        return iniciative.insert();
    },

    update: function (data) {
        var iniciative = new GlideRecord(this.table);
        iniciative.get(data.sys_id);
        for (var id in data) {
            iniciative[id] = data[id];
        }
        return iniciative.update();
    },

    delete: function (id) {
        var iniciative = new GlideRecord(this.table);
        iniciative.get(id);
        return iniciative.deleteRecord();
    },

    where: function (field, condition, value) {
        this.iniciative = new GlideRecord(this.table);
        if (typeof value === 'undefined') {
            this.iniciative.addQuery(field, condition);
            return this;
        }
        this.iniciative.addQuery(field, condition, value);
        return this;
    },

    query: function () {
        this.iniciative.query();
        return this.iniciative;
    },

    type: 'IPSModelFactory'
};
