sys_script_include.script:
var IPSPerformanceEvaluationController = Class.create();
IPSPerformanceEvaluationController.prototype = {
    initialize: function () {
        this.evaluationModel = new IPSEvaluationsModel();
        this.objetiveModel = new IPSObjetiveModel();
    },

    getEvaluation: function (id) {
        var evaluation = this.evaluationModel.find(id);


        return {
            user: {
                name: evaluation.assigned_to.name + '',
                sys_id: evaluation.assigned_to.sys_id + '',
                settings: evaluation.collaborator_settings.getDisplayValue() ? JSON.parse(evaluation.collaborator_settings.getDisplayValue()) : { reject_reason: '' },

            },
            lead: {
                name: evaluation.leader.name + '',
                sys_id: evaluation.leader.sys_id + '',
                settings: evaluation.leader_settings.getDisplayValue() ? JSON.parse(evaluation.leader_settings.getDisplayValue()) : { comments: '' },
            },
            pdf: evaluation.download_self_assessment.getDisplayValue(),
            state: evaluation.state + '',
            reason_for_rejection: evaluation.reason_for_rejection + '',
            objetives: this.objetiveModel
                .getObjetives(id)
                .withRelationShip('initiatives')
                .get(),
            my_colaborator_comments: evaluation.my_colaborator_comments + '',
            my_leader_comments: evaluation.my_leader_comments + '',
            ye_colaborator_comments: evaluation.ye_colaborator_comments + '',
            ye_leader_comments: evaluation.ye_leader_comments + '',
            actions_objectives_patch_colaborator: evaluation.actions_objectives_patch_colaborator + '',
            actions_objectives_patch_leader: evaluation.actions_objectives_patch_leader + '',
            leader_averange_evaluation: evaluation.leader_averange_evaluation + '',
            collaborator_averange_evaluation: evaluation.collaborator_averange_evaluation + ''
        }

    },


    type: 'IPSPerformanceEvaluationController'
};
