sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {


    var evaluationModel = new IPSEvaluationsModel();

    var data = {
        sys_id: request.pathParams.sys_id,
        my_colaborator_comments: request.body.data.my_colaborator_comments,
        actions_objectives_patch_colaborator: request.body.data.actions_objectives_patch_colaborator,
        my_leader_comments: request.body.data.my_leader_comments,
        actions_objectives_patch_leader: request.body.data.actions_objectives_patch_leader,
        state: 50
    };


    if (
        !request.body.data.colaborator
    ) {
        var today = new GlideDateTime();
        var send_notifications = JSON.parse(gs.getProperty('x_ipsu2_ips_perfor.ips.status_ye_evaluation_notifications'));
        var start_my = new GlideDateTime(gs.getProperty('x_ipsu2_ips_perfor.ips.evaluation_period.ye_start') + ' 00:00:00');
        var end_my = new GlideDateTime(gs.getProperty('x_ipsu2_ips_perfor.ips.evaluation_period.ye_end') + ' 00:00:00');
        if (send_notifications && today.compareTo(start_my) >= 0 && today.compareTo(end_my) <= 0) {
            data.is_active = true;
        }

        data.state = 60;
    }

    evaluationModel.update(data);

    return 'okey';

})(request, response);
