sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    // implement resource here

    var evaluations = new global.IPSEvaluationsController(request.body.data.evaluacion.evaluation_id);
    var user_data = [];
    var evaluation = evaluations.getEvaluation();

    var u_show_information = false;
    if (gs.getUserID() == evaluations.evaluationModel.evaluation.assigned_to.getValue()) {

        u_show_information = evaluations.evaluationModel.evaluation.u_show_information.getValue() == 0 ? false : true;
    } else {
        u_show_information = evaluations.evaluationModel.evaluation.u_show_information_manager.getValue() == 0 ? false : true;
    }

    var lider = evaluation.u_jefe_del_empleado;
    if (gs.getUserID() == evaluations.evaluationModel.evaluation.u_jefe_del_empleado.getValue()) {
        lider = {
            name: evaluations.evaluationModel.evaluation.u_jefe_del_empleado.name.getValue(),
            sys_id: evaluations.evaluationModel.evaluation.u_jefe_del_empleado.sys_id.getValue(),
        };
    } else {




        var g = new GlideRecord('sys_user_delegate');
        g.addQuery('user', lider.sys_id);
        g.addQuery('approvals', 'true');
        g.addQuery('starts', '<=', gs.daysAgo(0));
        g.addQuery('ends', '>=', gs.daysAgo(0));
        g.orderByDesc('sys_created_on');
        g.query();
        if (g.next()) {

            lider = {
                name: g.delegate.name,
                sys_id: g.delegate.sys_id,
            };

        }
    }





    return {
        u_averange_evaluation: evaluations.evaluationModel.evaluation.u_averange_evaluation.getValue() || 0,

        is_active: evaluations.evaluationModel.evaluation.u_is_active.getValue() == 1 ? true : false,
        u_show_information: u_show_information,
        test: evaluations.evaluationModel.evaluation.u_my_thecnical_leader_comments.getValue(),
        comentarios_manager: evaluations.evaluationModel.evaluation.u_manager_settings.getValue(),
        comentarios: evaluations.evaluationModel.evaluation.u_motivo_de_rechazo.getValue(),
        colaborador: evaluation.assigned_to,
        liber: lider,
        stepper: evaluations.getStepper(),
        user_data: user_data,
        objetives: evaluation.objetives,
        state: evaluation.state,
        u_my_colaborator_comments: evaluations.evaluationModel.evaluation.u_my_colaborator_comments.getValue(),
        u_my_thecnical_leader_comments: evaluations.evaluationModel.evaluation.u_my_thecnical_leader_comments.getValue(),

        u_actions_objectives_patch_colaborator: evaluations.evaluationModel.evaluation.u_actions_objectives_patch_colaborator.getValue(),
        u_actions_objectives_patch_thecnical_leader: evaluations.evaluationModel.evaluation.u_actions_objectives_patch_thecnical_leader.getValue(),
        state_valiadations: false,

        u_ye_colaborator_comments: evaluations.evaluationModel.evaluation.u_ye_colaborator_comments.getValue(),
        u_ye_thecnical_leader_comments: evaluations.evaluationModel.evaluation.u_ye_thecnical_leader_comments.getValue(),
    };

})(request, response);
