sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    var initiativeModel = new IPSInitiativesModel();
    var evaluationModel = new IPSEvaluationsModel();

    var iniciativa = initiativeModel.find(request.pathParams.sys_id);

    evaluationModel.update({
        sys_id: iniciativa.goal.evaluation.sys_id + '',
        work_notes: 'Se elimina la iniciativa   ' + iniciativa.no_iniciative +
            '\nQue pertenece al objetivo:  ' + iniciativa.goal.no_goal + '.- ' + iniciativa.goal.target +
            '\n\n' + 'Iniciativa: ' + iniciativa.no_iniciative + '.- ' + iniciativa.iniciative + '\n' +
            'Entregable: ' + iniciativa.deliverable + '\n' +
            'Fecha de entrega: ' + new GlideDateTime(iniciativa.work_end).getDate() + '\n' +
            'Peso de la iniciativa: ' + iniciativa.collaborator_initiative_weight + '\n' +
            'Fecha de eliminaciÃ³n: ' + (new GlideDateTime()).getDisplayValue()
    });


    initiativeModel.delete(request.pathParams.sys_id);
    return request.pathParams.sys_id;







})(request, response);
