sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    var initiativeModel = new IPSInitiativesModel();

    if (!request.body.data.sys_id) {
        return initiativeModel.create(request.body.data)
    }


    var iniciative = initiativeModel.find(request.body.data.sys_id);
    var evaluatioModel = new IPSEvaluationsModel();

    evaluatioModel.update({
        sys_id: iniciative.goal.evaluation.sys_id + '',
        work_notes: 'Se modifico la iniciativa ' + iniciative.no_iniciative + '\n' +
            ' Que pertenece al objetivo:  ' + iniciative.goal.no_goal + '.- ' + iniciative.goal.target +
            '-------------------------\n' +
            '\n\nEra: \n' +
            'Iniciativa: ' + iniciative.no_iniciative + '.- ' + iniciative.iniciative + '\n' +
            'Entregable: ' + iniciative.deliverable + '\n' +
            'Fecha de entrega: ' + new GlideDateTime(iniciative.work_end).getDate() + '\n' +
            'Peso de la iniciativa: ' + iniciative.collaborator_initiative_weight +
            '\n-------------------------\n' +
            '\n\nAhora es: ' +
            'Iniciativa: ' + iniciative.no_iniciative + '.- ' + request.body.data.iniciative + '\n' +
            'Entregable: ' + request.body.data.deliverable + '\n' +
            'Fecha de entrega: ' + new GlideDateTime(request.body.data.work_end).getDate() + '\n' +
            'Peso de la iniciativa: ' + request.body.data.collaborator_initiative_weight
    });
    return initiativeModel.update(request.body.data);

})(request, response);
