sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    var evaluationModel = new IPSEvaluationsModel();
    var objetiveModel = new IPSObjetiveModel();
    var iniciativeModel = new IPSInitiativesModel();
    var date = new GlideDateTime();

    var objetive = objetiveModel.find(request.pathParams.sys_id);

    var comments = 'Se elimina el Objetivo   ' + objetive.no_goal +
        '\n\n' + 'Objetivo ' + objetive.no_goal + ' : ' + objetive.target + '\n' +
        'Peso del objetivo: ' + objetive.collaborator_goal_weight + '\n' +
        'Fecha de eliminación: ' + date.getDisplayValue() + '\nAdicional se eliminan las iniciativas asocioadas\n';

    var iniciatives = iniciativeModel.where('goal', objetive.sys_id).query();

    while (iniciatives.next()) {

        comments += '----------------------------------------\nSe elimina la Iniciativa   ' + iniciatives.u_no_iniciativa +
            '\n\n' + 'Iniciativa ' + iniciatives.u_no_iniciativa + ' : ' + iniciatives.short_description + '\n' +
            'Entregable: ' + iniciatives.description + '\n' +
            'Fecha de entrega: ' + iniciatives.work_end + '\n' +
            'Peso de la iniciativa: ' + iniciatives.u_initiative_weight + '\n' +
            'Fecha de eliminación: ' + date.getDisplayValue() + '\n';
        iniciatives.deleteRecord();
    }

    objetive.deleteRecord();

    return evaluationModel.update({
        sys_id: objetive.sys_id,
        work_notes: comments
    });


})(request, response);
