sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    var today = new GlideDateTime();
    var send_notifications = JSON.parse(gs.getProperty('x_ipsu2_ips_perfor.ips.status_my_evaluation_notifications'));

    var start_my = new GlideDateTime(gs.getProperty('x_ipsu2_ips_perfor.ips.evaluation_period.my_start') + ' 00:00:00');
    var end_my = new GlideDateTime(gs.getProperty('x_ipsu2_ips_perfor.ips.evaluation_period.my_end') + ' 00:00:00');

    var evaluationModel = new IPSEvaluationsModel();
    var evaluacion = evaluationModel.find(request.pathParams.sys_id);
    var collaborator_settings = evaluacion.collaborator_settings.getDisplayValue() ?
        JSON.parse(evaluacion.collaborator_settings.getDisplayValue()) :
        { reject_reason: request.body.data.comment };

    var state = 20;
    var is_active = false;

    if (request.body.data.response == 'Aprobado') {
        if (send_notifications && today.compareTo(start_my) >= 0 && today.compareTo(end_my) <= 0) {
            is_active = true;
        }
        collaborator_settings.reject_reason = '';
        collaborator_settings = JSON.stringify(collaborator_settings);
        state = 40;
    }


    evaluationModel.update({
        sys_id: request.pathParams.sys_id,
        collaborator_settings: JSON.stringify(collaborator_settings),
        is_active: is_active,
        state: state,
        work_notes: gs.getMessage("Rejected for: {0}", gs.getUserName()) + '\n\n' + gs.getMessage('Comment: {0}', request.body.data.comment)
    });

    var aprobacion = new GlideRecord('sysapproval_approver');
    aprobacion.addQuery('approver', evaluacion.u_jefe_del_empleado);
    aprobacion.addQuery('sysapproval', evaluacion.sys_id);
    aprobacion.setLimit(1);
    aprobacion.orderByDesc('sys_created_on');
    aprobacion.query();
    if (aprobacion.next()) {

        if (request.body.data.response == 'Aprobado') {
            aprobacion.state = 'approved';
        } else {
            aprobacion.state = 'rejected';
        }

        aprobacion.comments.setJournalEntry(request.body.data.comment);
        aprobacion.update();
    }

    return request.body.data;
})(request, response);
