sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    // implement resource here

    var objetiveModel = new IPSObjetiveModel();

    if (!request.body.data.objetivo_id)
        return objetiveModel.create({
            target: request.body.data.objetivo,
            collaborator_goal_weight: request.body.data.peso,
            evaluation: request.body.data.evaluacion_id,
            no_goal: String(request.body.data.u_no_objective)
        });


    var objetive = objetiveModel.find(request.body.data.objetivo_id);

    var evaluationModel = new IPSEvaluationsModel();
    evaluationModel.update({
        sys_id: request.body.data.evaluacion_id,
        work_notes: 'Se modifico el Objetivo   ' + objetive.no_goal +
            '\n\nEra: \n' +
            '-------------------------\n' +
            'Objetivo ' + objetive.no_goal + ' : ' + objetive.target + '\n' +
            'Peso del objetivo: ' + objetive.collaborator_goal_weight +
            '\n\nAhora es: ' +
            '\n-------------------------\n' +
            'Objetivo ' + objetive.no_goal + ' : ' + request.body.data.objetivo + '\n' +
            'Peso del objetivo: ' + request.body.data.peso + '\n\n' +
            'Modificado el: ' + (new GlideDateTime()).getDisplayValue()
    });

    var objetive = objetiveModel.update({
        sys_id: request.body.data.objetivo_id,
        target: request.body.data.objetivo,
        collaborator_goal_weight: request.body.data.peso
    });


    return objetive;



})(request, response);
